mapper.select:

select title,content,person_no,no,cloth_no,to_char( trunc(reg_date), 'YYYY/MM/DD' ) regDate
from market
where no=#{no}

mapper.update:
update market
set title=#{title}, content=#{content}, cloth_no=#{cloth_no}
where no=#{no}

mapper.select:

select img_no
from cloth
where no=#{mo}


select db_name
from img
where no=#{fileNo}
