mapper.select:
select no, otherNo, ownername, otherNicname, profile, ownerprofile, codi, likes, choose, nvl2(like_person.person_no, 1, 0 ) likeflag from like_person, (select codi.no no, p2.no otherNo, p2.nicname otherNicname, person.nicname ownername, pro_img.db_name profile, owner_img.db_name ownerprofile, codiimg.db_name codi, choose, NVL(likes, 0) likes from codi, person, img codiimg, img pro_img, img owner_img, person p2, (select codi_no like_codi_no, count(like_person.person_no) likes from like_person group by codi_no) where p2.no = codi.person_no and person.no = codi.other_no and other_no=#{num}
and codi.person_no = #{num}
and codi.person_no != #{num} and codi.person_no != 0
and codi.person_no = 0
and codi_no = codiimg.no and p2.profile_no = pro_img.no and person.profile_no = owner_img.no and codi.no = like_codi_no(+) order by codi.no desc) where no =like_person.CODI_NO(+) and #{authNo}=like_person.PERSON_NO(+)

