mapper.select:
select no, otherNo, otherNicname, profile, codi, likes, choose, nvl2(like_person.person_no, 1, 0 ) likeflag from like_person, (select codi.no no, person.no otherNo, p2.nicname otherNicname, pro_img.db_name profile, codiimg.db_name codi, choose, NVL(likes, 0) likes from codi, person, img codiimg, img pro_img, person p2, (select codi_no like_codi_no, count(like_person.person_no) likes from like_person group by like_person.person_no, codi_no) where p2.no = codi.person_no and person.no = codi.other_no and other_no=#{num}
and codi.person_no = #{num}
and codi.person_no != #{num}
and codi_no = codiimg.no and p2.profile_no = pro_img.no and codi.no = like_codi_no(+) order by codi.no desc) where no =like_person.CODI_NO(+) and #{authNo}=like_person.PERSON_NO(+)

mapper.update:

update codi
set choose = 1
where no = #{no}

